package com.k2web.k2webboard.controller;import com.k2web.k2webboard.model.UserDTO;import com.k2web.k2webboard.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpSession;@Controller@RequestMapping("/user")public class UserController {    @Autowired    private UserService userService;    @RequestMapping(value = "/login", method = RequestMethod.POST)    private String login(String username, String password, HttpSession session) {        UserDTO u = new UserDTO();        u.setUsername(username);        u.setPassword(password);        if (userService.logIn(u) != null)  {            session.setAttribute("logIn", u);            return "redirect:/board/selectAll";        } else {            return "redirect:/";        }    }    @RequestMapping("/register")    private String toRegister() {        return "user/register";    }    @RequestMapping(value = "/registerlogic", method = RequestMethod.POST)    private String register(UserDTO u) {        userService.register(u);        return "redirect:/";    }}