package com.k2web.k2webboard.repository;import com.k2web.k2webboard.model.BoardDTO;import org.apache.ibatis.session.SqlSession;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.List;@Repositorypublic class BoardRepository {    private final String NAMESPACE = "mapper.BoardMapper";    @Autowired    SqlSession sqlSession;    public void insert(BoardDTO b) {        sqlSession.insert(NAMESPACE + ".insert", b);    }    public List<BoardDTO> selectAll(HashMap<String, Integer> map) {        return sqlSession.selectList(NAMESPACE + ".selectAll", map);    }    public BoardDTO selectOne(int id) {        return sqlSession.selectOne(NAMESPACE + ".selectOne", id);    }    public void update(BoardDTO b) {        sqlSession.update(NAMESPACE + ".update", b);    }    public void delete(int id) {        sqlSession.delete(NAMESPACE + ".delete", id);    }    public void init(BoardDTO b) {        sqlSession.insert(NAMESPACE + ".init", b);    }    public int count() {        return sqlSession.selectOne(NAMESPACE, ".count");    }}