package com.k2web.k2webboard.repository;import com.k2web.k2webboard.model.ReplyDTO;import org.apache.ibatis.session.SqlSession;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic class ReplyRepository {    private final String NAMESPACE = "mapper.ReplyMapper";    @Autowired    private SqlSession sqlSession;    public void insert(ReplyDTO r) {        sqlSession.insert(NAMESPACE + ".insert", r);    }    public List<ReplyDTO> selectAll(int boardId) {        return sqlSession.selectList(NAMESPACE + ".selectAll", boardId);    }}