package com.k2web.k2webboard.repository;import com.k2web.k2webboard.model.BoardDTO;import com.k2web.k2webboard.model.UserDTO;import org.apache.ibatis.session.SqlSession;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.List;@Repositorypublic class UserRepository {    private final String NAMESPACE = "mapper.UserMapper";    @Autowired    private SqlSession sqlSession;    public UserDTO auth(UserDTO u) {        return sqlSession.selectOne(NAMESPACE + ".auth", u);    }    public void register(UserDTO u) {        sqlSession.insert(NAMESPACE + ".register", u);    }    public UserDTO selectOne(int userId) {        return sqlSession.selectOne(NAMESPACE + ".selectOne", userId);    }}