package com.k2web.k2webboard.service;import com.k2web.k2webboard.model.BoardDTO;import com.k2web.k2webboard.repository.BoardRepository;import lombok.RequiredArgsConstructor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;@Service@RequiredArgsConstructorpublic class BoardService {    @Autowired    private final BoardRepository boardRepository;    private final int PAGE_SIZE = 10;    public List<BoardDTO> selectAll(int pageNo) {        HashMap<String, Integer> map = new HashMap<>();        int startNum = (pageNo - 1) * PAGE_SIZE;        map.put("startNum", startNum);        map.put("PAGE_SIZE", PAGE_SIZE);        return boardRepository.selectAll(map);    }    public int selectLastPage() {        int total = boardRepository.count();        if(total % PAGE_SIZE == 0) {            return total / PAGE_SIZE;        } else {            return (total / PAGE_SIZE) + 1;        }    }    public BoardDTO selectOne(int id) {        return boardRepository.selectOne(id);    }}